**Solution 3 - Two Pointers - O(mlogm + nlogn)**
```
class Solution {
public:
    vector<int> intersect(vector<int>& nums1, vector<int>& nums2) {
        int fst = 0, snd = 0;
        vector <int> intsec;

        sort(nums1.begin(), nums1.end());
        sort(nums2.begin(), nums2.end());

        while (fst < nums1.size() && snd < nums2.size()) {
            if (nums1[fst] == nums2[snd]) {
                intsec.push_back(nums1[fst]);
                fst++;
                snd++;
            } else if (nums1[fst] < nums2[snd]) {
                fst++;
            } else {
                snd++;
            }
        }

        return intsec;
    }
};
