class Solution {
public:

    int binarySearch(int target, vector <int> arr, int left, int right) {
        while (left < right) {
            int mid = (left + right) / 2;
            if (arr[mid] == target) {
                right = mid;
            } else if (arr[mid] > target) {
                right = mid;
            } else {
                left = mid + 1;
            }
        }

        if (left >= 0 && left < arr.size() && arr[left] == target) {
            return left;
        }

        return -1;
    }

    vector<int> intersect(vector<int>& nums1, vector<int>& nums2) {
        int fst = 0, snd = 0;
        vector <int> intsec;

		// assume sorted

        if (nums1.size() > nums2.size()) {
            vector <int> temp = nums1;
            nums1 = nums2;
            nums2 = temp;
        }

        int left = 0, right = nums2.size();
        for (int i = 0; i < nums1.size(); i++) {

            int index = binarySearch(nums1[i], nums2, left, right);

            if (index != -1) {
                left = index + 1;
                intsec.push_back(nums2[index]);
            }
        }

        return intsec;
    }
};
