class Solution {
public:
    bool checkcondition(vector<int>& nums2,int target,unordered_map<int,int>& mp)
    {
        int low=0,high=nums2.size()-1,mid=low+(high-low)/2;
        while(low<=high)
        {
            mid=low+(high-low)/2;      // whynot ?(low+high)/2;try to avoid Runtime(overflow) error always be in safe side  
            if(nums2[mid]==target && mp[nums2[mid]]>0)
            {
                mp[nums2[mid]]--;
                return true;
            }
            else if(nums2[mid]>target)
            {
                high=mid-1;
            }
            else
            {
                low=mid+1;
            }
        }
        return false;
    }
    vector<int> intersect(vector<int>& nums1, vector<int>& nums2) 
    {
        unordered_map<int,int> mp;
        for(auto e:nums2)
        {
            mp[e]++;
        }
        vector<int> ans;
        sort(nums2.begin(),nums2.end());
        for(int i=0;i<nums1.size();i++)
        {
            if(checkcondition(nums2,nums1[i],mp))
            {
                ans.push_back(nums1[i]);
            }
        }

        return ans;
    }
};
```

