class Solution {
    public int[] intersect(int[] nums1, int[] nums2) {

        Arrays.sort(nums1);
        Arrays.sort(nums2);
        ArrayList<Integer> list=new ArrayList();
        int m=nums1.length-1,n=nums2.length-1;
        while(m>-1 && n>-1)
        {
            if(nums1[m]<nums2[n])
            {n--;}
            else if(nums1[m]>nums2[n])
            {m--;}
            else
