class Solution {
public:

    // Hashing
    vector<int> intersect(vector<int>& nums1, vector<int>& nums2) {
        vector<int>arr;
        map<int,int>mp;
        if(nums1.size()>nums2.size()){
            vector<int>tmp=nums1;
            nums1=nums2;
            nums2=tmp;
        }
        for(int i:nums1) mp[i]++;
        for(int j:nums2){
            if(mp.find(j)!=mp.end() && mp[j]>0){
                arr.push_back(j);
                mp[j]--;
            }
        }
        return arr;
    }
};
