class Solution {
public:
    vector<int> intersect(vector<int>& nums1, vector<int>& nums2) {
        vector<int> res;

        // if the arrays are sorted
        sort(nums1.begin(), nums1.end());
        sort(nums2.begin(), nums2.end());

        //if nums1's size is small compared to nums2's size? Which algorithm is better?
        //binary search
        vector<int> shorter, longer;

        if(nums1.size() <= nums2.size()){
            shorter = nums1;
            longer = nums2;
        }else{
            shorter = nums2;
            longer = nums1;            
        }

        int leftInd = 0;
        int rightInd = longer.size() - 1;
        int ind;

        for(int i = 0; i < shorter.size(); ++i){
            ind = binarySearch(longer, leftInd, rightInd, shorter[i]);
            if(ind != -1){
                res.push_back(shorter[i]);
                leftInd = ind + 1;
            }
          }

        return res;
    }

    int binarySearch(vector<int> &nums, int start, int end, int target){
        //int start = 0, end = nums.size() - 1;

        while(start <= end){
            int mid = start + (end - start) / 2;
            if(nums[mid] == target){
                while(mid > start && nums[mid-1] == target) --mid;
                return mid;
            }     
            else if(nums[mid] < target){
                start = mid + 1;
            }else{
                end = mid - 1;
            }         
        }
        return -1;
    }
};
