class Solution {
public:
    vector<int> intersect(vector<int>& nums1, vector<int>& nums2) {
        unordered_map<int,int> mp1, mp2;
        vector<int> ans;
        for(int i=0;i<nums1.size();i++)
            mp1[nums1[i]]++;

        for(int i=0;i<nums2.size();i++)
            mp2[nums2[i]]++;
        for(auto a:mp1)
        {
            int x=a.first;
            int y=a.second;
            int c=min(y, mp2[x]);
            while(c--)
            {
                ans.push_back(x);
            }
        }
        return ans;
    }
};
```
