class Solution {
    func intersect_TwoPointers(_ nums1: [Int], _ nums2: [Int]) -> [Int] {
        let nums1 = nums1.sorted()
        let nums2 = nums2.sorted()
        let n1 = nums1.count
        let n2 = nums2.count
        var i1 = 0
        var i2 = 0
        var result = [Int]()

        while i1 < n1 && i2 < n2 {
            if nums1[i1] == nums2[i2] {
                result.append(nums1[i1])
                i1 += 1
                i2 += 1
            } else if nums1[i1] > nums2[i2] {
                i2 += 1
            } else {
                i1 += 1
            }
        }

        return result
    }

    func intersect_HashMap(_ nums1: [Int], _ nums2: [Int]) -> [Int] {
        var map = [Int: Int]()
        var result = [Int]()

        for i in 0..<nums1.count {
            if let value = map[nums1[i]] {
                map[nums1[i]] = value + 1
            } else {
                map[nums1[i]] = 1
            }
        }
        for i in 0..<nums2.count {
            if let value = map[nums2[i]] {
                map[nums2[i]] = value - 1
                if value - 1 >= 0 {
                    result.append(nums2[i])
                }
            }
        }

        return result
    }

    func intersect_BinarySearch(_ nums1: [Int], _ nums2: [Int]) -> [Int] {
        if nums1.count == 0 || nums2.count == 0 {
            return []
        }

        let nums1 = nums1.sorted()
        let nums2 = nums2.sorted()
        var result = [Int]()
        var left = 0

        for i in 0..<nums1.count {
            if left >= nums2.count {
                break
            }
            left = binarySearch(left, nums1[i], nums2)
            if left < nums2.count && nums1[i] == nums2[left] {
                result.append(nums1[i])
                left += 1
            }
        }

        return result
    }

    private func binarySearch(_ left: Int, _ num: Int, _ nums: [Int]) -> Int {
        var left = left
        var right = nums.count - 1
        var middle = 0

        while left < right {
            middle = (left + right) / 2
            if nums[left] == num {
                return left
            }
            if nums[middle] < num {
                left = middle + 1
            } else if nums[middle] > num {
                right = middle
            } else {
                if middle - 1 >= left && nums[middle - 1] == num {
                    right = middle - 1
                } else {
                    return middle
                }
            }
        }

        return left
    }
}
