class Solution {
public:
    vector<int> intersect(vector<int>& nums1, vector<int>& nums2) {

        vector<int>result; // Creating a new vector to store the output.

        sort(nums1.begin(), nums1.end()); // Sorting the first array.
        sort(nums2.begin(), nums2.end()); // Sorting the second array.

        /* 
           If both arrays only contain one element then return any of the one array element.
           If both arrays are empty then return {}.
        */

        // Start of if statement:
        if(nums1.size() == 1 && nums2.size() == 1)
        {
            if(nums1[0] == nums2[0])
            {
                return {nums1[0]};
            }

            else 
            {
                return {};
            }

        }
        // End of if statement.

        /* 
           Now we will use two variables to check whether the first element of the first array is equal or not.
           If equal then push it at the back of the result array and increment both the arrays.
           If the first element is less than the first element of the second array then increment the first array 
           otherwise increment the second array.        
        */

        int i = 0, j = 0;

        // Start of while loop:
        while (i < nums1.size() && j < nums2.size())
        {
            if(nums1[i] == nums2[j])
            {
                result.push_back(nums1[i]);
                i++;
                j++;
            }

            else if(nums1[i] < nums2[j])
            {
                i++;
            }

            else
            {
                j++;
            }
        }
        // End of while loop.

        return result; // Return the result.
    }
};

