class Solution {
    public int lengthOfLastWord(String s) {
        s = s.trim(); // Trim leading and trailing spaces
        int count = 0;

        for (int i = s.length() - 1; i >= 0; i--) {
            if (s.charAt(i) != ' ') {
                count++;
            } else {
                break; // Stop counting as soon as a space is encountered
            }
        }

        return count;
    }
}

