class Solution {
public:
    int addDigits(int n) {
        if(n == 0) // if number is already zero
        {
            return 0; // return zero
        }
        else if(n % 9 == 0) // if it is divisble by 9
        {
            return 9;
        }
        else
        {
            return n % 9; // if not divisible by 9 remainder number % 9
        }
    }
};
```
