class Solution {
public:
    int addDigits(int num) {
        // Run this loop till num is atleast of two digit number
        while(num/10){
            int digitSum = 0;
            // Add all the digits of num to "digitSum"
            while(num){
                digitSum += num % 10;
                num /= 10;
            }
            // Assign this new Value to num again to repeat this process until num becomes a single digit integer
            num = digitSum;
        }
        return num;
    }
};
