class Solution {
    public int addDigits(int num) {
        int sum = 0;
        String x = num+"";
        while(x.length() > 1){
            for(int i = x.length()-1; i >= 0; i--){
                int c = x.charAt(i);
                c = c - '0';
                sum += c;
            }
            x = sum+"";
            sum = 0;
        }
        return x.charAt(0)-'0';
