#include<stdio.h>

int addDigits(int num) {
    int sum = 0;
    while (num > 0) {
        sum += num % 10;
        num /= 10;
    }
    return sum;
}
int main() {
    int input;
    printf("Enter a number: ");
    scanf("%d", &input);

    if (input < 10) {
        printf("Please enter a two-digit number: ");
        scanf("%d", &input);
    }

    int result = addDigits(input);
    printf("Result: %d\n", result);

    return 0;
}

