class Solution {
    func generateMatrix(_ n: Int) -> [[Int]] {

        var m = [[Int]](repeating: [Int](repeating: 0, count: n), count: n)

        var l = 0, r = n - 1, t = 0, b = n - 1, x = 0, y = 0, val = 1

        while t <= b {

            x = l; y = t
            while x <= r { m[t][x] = val; val += 1; x += 1 }

            t += 1; y = t
            while y <= b { m[y][r] = val; val += 1; y += 1 }

            r -= 1; x = r
            while x >= l { m[b][x] = val; val += 1; x -= 1 }

            b -= 1; y = b
            while y >= t { m[y][l] = val; val += 1; y -= 1 }

            l += 1
        }

        return m
    }
}
