class Solution {
    public int maxSumSubmatrix(int[][] matrix, int target) {
        int m = matrix.length;
        int n = matrix[0].length;
        int[][] dp = new int[m + 1][n + 1];
        int max = Integer.MIN_VALUE;
        for(int i = 0; i <m; ++i) {
            for(int j = 0; j < n; ++j) {
                dp[i + 1][j + 1] = dp[i][j + 1] + dp[i + 1][j] - dp[i][j] + matrix[i][j];
                if(dp[i + 1][j + 1] <= target) max = Math.max(max, dp[i + 1][j + 1]);
            }
        }

        int sum = 0;
        for(int i = 1; i <= m; ++i) {
            for(int j = 1; j <= n; ++j) {
                for(int k = 1; i - k >= 0; ++k) {
                    for(int l = 1; j - l >= 0; ++l) {
                        sum = dp[i][j] - dp[i - k][j] - dp[i][j - l] + dp[i - k][j - l];
                        if(sum <= target) max = Math.max(max, sum);
                    }
                }
            }
        }
        return max;
    }
}
