class Solution:
    def maxSumSubmatrix(self, matrix: List[List[int]], k: int) -> int:
        R, C = len(matrix), len(matrix[0])
        if R > C:
            n_matrix = [[matrix[r][c] for r in range(R)] for c in range(C)]
            R, C = C, R
            matrix = n_matrix

        ps = [[0] * (C + 1) for _ in range(R + 1)]

        for r in range(R):
            for c in range(C):
                ps[r+1][c+1] = ps[r+1][c] + matrix[r][c]
        for c in range(C):
            for r in range(R):
                ps[r+1][c+1] = ps[r][c+1] + ps[r+1][c+1]

        res = -sys.maxsize
        for r1 in range(R):
            for r2 in range(r1, R):                
                vals = []
                maxx = -sys.maxsize
                flag = False
                for i in range(C + 1): # get the horizontal partial sums
                    val = ps[r2+1][i] - ps[r1][i]
                    if val - maxx <= k:
                        flag = True
                    maxx = max(maxx, val)
                    vals.append(val)

                pairs = sorted([(v, i) for i, v in enumerate(vals)], reverse = True)
                stack = []
                i = 0
                for j in range(len(pairs)):
                    # elements with the value <= pairs[j][0] - k should be enstacked
                    while i < len(pairs) and pairs[i][0] >= pairs[j][0] - k:
                        while stack and stack[-1] >= pairs[i][1]:
                            stack.pop()
                        stack.append(pairs[i][1])
                        i += 1

                    pos = bisect.bisect_left(stack, pairs[j][1])
                    if pos > 0:
                        res = max(res, pairs[j][0] - vals[stack[pos-1]])
        return res
