    int maxSumSubmatrix(vector<vector<int>>& matrix, int k) {
        K=k, m=matrix.size(), n=matrix[0].size();
        if(n > m){   // columns greater than rows 
            vector<int> rowSum(n);   // sqeeze rows into a single array
            for(int i=0; i<m; i++){
                fill(rowSum.begin(), rowSum.end(), 0);
                for(int row=i; row<m; row++){    // convert from ith row till last row into a single 1D array
                    for(int col=0; col<n; col++){
