	// O(nlogn)
	public static int maxSubArrayK(int[] temp, int k) {

		int sum = kadanes(temp);
		if (sum <= k)
			return sum;
		TreeSet<Integer> set = new TreeSet<>();
		set.add(0);
		int csum = 0, len = temp.length, max = Integer.MIN_VALUE;
		for (int i = 0; i < len; i++) {
			csum += temp[i];
			Integer set2 = set.ceiling(csum - k);
			if (set2 != null) {
				if (csum - set2 > max)
					max = csum - set2;
			}
			set.add(csum);
		}
		return max;
	}

	// Kadanes
	// O(n)
	public static int kadanes(int[] temp) {
		int omax = temp[0], cmax = temp[0], len = temp.length;
		for (int i = 1; i < len; i++) {
			cmax += temp[i];
			if (temp[i] > cmax)
				cmax = temp[i];
			if (cmax > omax)
				omax = cmax;
		}
		return omax;
	}

