```c++
class Solution {
public:
    int maxSumSubmatrix(vector<vector<int>>& matrix, int k) {
        uint8_t m = matrix.size();
        uint8_t n = matrix[0].size();

        int psa[m][n];
        psa[0][0] = matrix[0][0];

        for(uint8_t i = 1; i < m; i++)
            psa[i][0] = psa[i-1][0] + matrix[i][0];
        for(uint8_t i = 1; i < n; i++)
            psa[0][i] = psa[0][i-1] + matrix[0][i];
        for (uint8_t i = 1; i < m; i++)
            for (uint8_t j = 1; j < n; j++)
                psa[i][j] = psa[i - 1][j] + psa[i][j - 1] - psa[i - 1][j - 1] + matrix[i][j];

        int maxSum = INT_MIN;
        for(uint8_t i = 0; i < m; i++)
            for(uint8_t j = 0; j < n; j++)
                for(uint8_t p = i; p < m; p++)
                    for(uint8_t q = j; q < n; q++)
                    {
                        int x = psa[p][q] + 
                            (i > 0 ? -psa[i-1][q] : 0) +
                            (j > 0 ? -psa[p][j-1] : 0) +
                            (i > 0 && j > 0 ? psa[i-1][j-1] : 0);
                        if(x <= k)
                            maxSum = max(maxSum, x);
                    }

        return maxSum;
    }
};
