    int maxSumSubmatrix(vector<vector<int>>& matrix, int k) {
        int ans = INT_MIN;

        int R = matrix.size();
        int C = matrix[0].size();
        int arr[R];

        for(int l = 0; l < C; ++l) {
            memset(arr, 0, sizeof(arr));

            for(int r = l; r < C; ++r) 
            {
                for(int up = 0; up < R; ++up)
                {
                    arr[up] += matrix[up][r]; 
                }
                int sum = 0, g = INT_MIN;
                for(int up = 0; up < R; ++up) 
