                for (int row = 0; row < rows; row++)
                    sums[row] += matrix[row][r];                    

                for (int i = 0; i < rows; i++)
                {
                    int sum = 0;                             
                    for (int j = i; j < rows; j++)
                    {
                        sum += sums[j];
                        if (sum > max && sum <= k)
                            max = sum;
                    }                    
                }
            }
        }
        return max;
    }
}
