class Solution {
    public int maxSumSubmatrix(int[][] matrix, int k) {
        int n = matrix.length, m = matrix[0].length;
        int[][] prefix = calculatePrefix(matrix);
        int answer = -100_001;
        for (int sr = 0; sr < n; sr++) {
            for (int er = sr; er < n; er++) {
                for (int sc = 0; sc < m; sc++) {
                    for (int ec = sc; ec < m; ec++) {
                        //  find the sum of the rectangle [sr, sc] and [er, ec] both inclusive
                        int sum = prefix[er + 1][ec + 1] - prefix[sr][ec + 1] - prefix[er + 1][sc] + prefix[sr][sc];
                        if (sum <= k && sum > answer) {
                            answer = sum;
                        }
                    }
                }
            }
        }
        return answer;
    }

    public int[][] calculatePrefix(int[][] a) {
        int n = a.length, m = a[0].length;
        int[][] answer = new int[n+1][m+1];
        //  at answer[i][j] -> sum of submatrix starting at (0,0) ending at (i - 1) and (j - 1)
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                answer[i][j] = answer[i-1][j] + answer[i][j-1] - answer[i-1][j-1] + a[i-1][j-1];
            }
        }
        return answer;
    }
}

