impl Solution {
    pub fn max_sum_submatrix(matrix: Vec<Vec<i32>>, k: i32) -> i32 {
        let mut set = BTreeSet::<i32>::new();
        let (n, m) = (matrix.len(), matrix[0].len());
        (0..n).map(|i| {
            let mut acc = vec![0; m];
            matrix.iter().skip(i).map(|row| {
