        rows, cols = len(matrix), len(matrix[0])
        prefix_sum = [[0] * (cols + 1) for _ in range(rows + 1)] #take advantage of -1 indexing
        # build prefix sum matrix, keeping track of the sum above and left of each index
        for row in range(rows):
            sum_left = 0
            for col in range(cols):
                sum_above = prefix_sum[row - 1][col]
                sum_left += matrix[row][col]
                prefix_sum[row][col] = sum_above + sum_left

        # fix start and end row, and for each traverse across the columns
        max_sum_lk = float('-inf')
        for row1 in range(rows):
            for row2 in range(row1, rows):
                left_sums = [0]
                # binary search for a sum to the left that is as small as possible (because we are subtracting it),
                # such that the cumulative sum across all these columns minus that left sum is <= k
                for end_col in range(cols):
                    sum_here = prefix_sum[row2][end_col] - prefix_sum[row1 - 1][end_col]

                    ins = bisect_right(left_sums, sum_here - k)
                    if ins > 0 and left_sums[ins - 1] == sum_here - k:
                        return k
                    elif ins <= end_col:
                        max_sum_lk = max(max_sum_lk, sum_here - left_sums[ins])

                    insort(left_sums, sum_here)

        return max_sum_lk

