class Solution {
    public int find(int sum[][],int p,int j,int k)
    {
        int max=-10000000,s=0;
        TreeMap<Integer,Integer>map=new TreeMap<Integer,Integer>();
        map.put(0,-1);
        for(int i=0;i<sum.length;i++)
        {
            s=s+sum[i][p]-sum[i][j+1];
            if(map.containsKey(s-k))
            {
                    return k;
            }
            if(map.higherKey(s-k)!=null)
            {
                int y=map.higherKey(s-k);
                max=Math.max(max,s-y);
            }
            map.put(s,i);
        }
        return max;
    }
    public int maxSumSubmatrix(int[][] matrix, int k) {
        int ans=-1000000;
        int sum[][]=new int[matrix.length][matrix[0].length+1];
        for(int i=0;i<matrix.length;i++)
        {
            for(int j=matrix[0].length-1;j>=0;j--)
            {
                sum[i][j]=sum[i][j+1]+matrix[i][j];
            }
        }
        for(int j=matrix[0].length-1;j>=0;j--)
        {
            for(int p=j;p>=0;p--)
            {
                ans=Math.max(ans,find(sum,p,j,k));
                if(ans==k)
                    return k;
            }
        }
        return ans;
    }
}
