class Solution{
public:
    int maxSumSubmatrix(vector<vector<int>>& matrix, int k)
    {
        int m = matrix.size(), n = matrix[0].size();
        int sum[m][n], mx=INT_MIN;
        for(int i=0;i<m;i++)
        {
            for(int j=0;j<n;j++)
            {
                auto x = matrix[i][j];
                if(i>0 && j>0) x=x+sum[i-1][j]+sum[i][j-1]-sum[i-1][j-1];
                else if(i>0 && j<=0) x=x+sum[i-1][j];
                else if(i<=0 && j>0) x=x+sum[i][j-1];
                sum[i][j]=x;
                for(int r=0;r<=i;r++)
                {
                    for(int c=0;c<=j;c++)
                    {
                        auto chk = sum[i][j];
                        if(r>0 && c>0) chk=chk-sum[r-1][j]-sum[i][c-1]+sum[r-1][c-1];
                        else if(r>0 && c<=0) chk=chk-sum[r-1][j];
                        else if(r<=0 && c>0) chk=chk-sum[i][c-1];
                        if(chk<=k) mx=max(mx,chk);
                    }
                }
            }
        }
