const maxSumSubmatrix = (g, k) => {
    let [n, m, res] = [g.length, g[0].length, Number.MIN_SAFE_INTEGER];
    let dp = initialize2DArrayNew(n, m); // save 2D matrix pre sum
    for (let i = 0; i < n; i++) {
        for (let j = 0; j < m; j++) {
            let sum = g[i][j];
            if (i > 0) sum += dp[i - 1][j];
            if (j > 0) sum += dp[i][j - 1];
            if (i > 0 && j > 0) sum -= dp[i - 1][j - 1];
            dp[i][j] = sum;
            for (let r = 0; r <= i; r++) { // subrectangles sum calculation
                for (let c = 0; c <= j; c++) {
                    let curSum = dp[i][j];
                    if (r > 0) curSum -= dp[r - 1][j];
                    if (c > 0) curSum -= dp[i][c - 1];
                    if (r > 0 && c > 0) curSum += dp[r - 1][c - 1];
                    if (curSum <= k) res = Math.max(res, curSum);
                }
            }
        }
    }
    return res;
};

const initialize2DArrayNew = (n, m) => {
    let data = [];
    for (let i = 0; i < n; i++) {
        let tmp = Array(m).fill(0);
        data.push(tmp);
    }
    return data;
};
