    let [n, m, res] = [g.length, g[0].length, Number.MIN_SAFE_INTEGER];
    for (let i = 0; i < m; i++) {
        let sum = Array(n).fill(0);
        for (let j = i; j < m; j++) {
            for (let k = 0; k < n; k++) {
                sum[k] += g[k][j];
            }
            let curSum = 0;
            let ts = new TreeSet([0]);
            for (const x of sum) {
                curSum += x;
                let tmp = ts.ceiling(curSum - k); // java treeset ceiling, c++ set lower_bound
                if (tmp != undefined) res = Math.max(res, curSum - tmp);
                ts.add(curSum);
            }
        }
    }
    return res;
};
