public:
    bool isNumber(string s) {
        int current_stat = 1;
        for(int i = 0; i < s.size(); i++) {
            char type;
            if(s[i] == ' ') {
                type = 'b';
            } else if(s[i] >= '0' && s[i] <= '9') {
                type = 'n';
            } else if(s[i] == 'e') {
                type = 'e';
            } else if(s[i] == '.') {
                type = 'd';
            } else if(s[i] == '+' || s[i] == '-') {
                type = 's';
            } else {
                return false;
            }
            current_stat = DFA[current_stat][type];
        }
        if(current_stat == 3 || current_stat == 5 || current_stat == 8 || current_stat == 9) {
            return true;
        }
        return false;
    }
};

