        for (int i = 0 ; i < s.size() ; i ++ ) {
            if( s[i] >= '0' && s[i] <= '9' ) {
                if (schemaS == "" || schemaS.at(schemaS.size() - 1) != '5') {
                    schemaS += '5';                
                }
