```
class Solution {
public:
    bool isNumber(string s) {
        vector<vector<int>> F = vector<vector<int>>(8,vector<int>(6,-1));
        F[0][1] = 1;
        F[0][2] = 4;
        F[0][0] = 2;

        F[1][2] = 4;
        F[1][0] = 2;

        F[2][0] = 2;
        F[2][3] = 5;
        F[2][2] = 3;
        F[2][4] = 8;

        F[3][0] = 3;
        F[3][3] = 5;
        F[3][4] = 8;

        F[4][0] = 3;

        F[5][1] = 6;
        F[5][0] = 7;

        F[6][0] = 7;

        F[7][0] = 7;
        F[7][4] = 8;

        unordered_map<char,int> act;

        int i = (int) '0';
        for(int j = i; j < i+10; j++){
            act[(char) j] = 0;
        }
        act['+'] = 1;
        act['-'] = 1;
        act['.'] = 2;
        act['E'] = 3;
        act['e'] = 3;

        int cur = 0;
        for(int i = 0; i < s.length(); i++){
            auto it = act.find(s[i]);
            if(it == act.end()){
                return false;
            }
            cur = F[cur][it->second];
            if(cur == -1){
                return false;
            }
        }

        return (F[cur][4] == 8);
    }
};
