			if ( (str.charAt(i) < '0' || str.charAt(i) > '9')  && str.charAt(i) !='.' && str.charAt(i) != 'e' ) {

			        //we met + or - at the middle but in a correct configuration. So, we continue
				if (  (str.charAt(i) == '+' || str.charAt(i) == '-')  && e== i-1 && i!=end ) {  }

				else 	return false;
			}
		}
		return true;
	}

