class Solution {
public:
    bool isNumber (string s)
    {
        // state transitions.
        int automata [][8] = 
        {
            {2,3,-1,1,0},
            {2,3,-1,-1,-1},
            {2,3,4,-1,8},
            {5,-1,4,-1,8},
            {7,-1,-1,6,-1},
            {5,-1,4,-1,8},
            {7,-1,-1,-1,-1},
            {7,-1,-1,-1,8},
            {-1,-1,-1,-1,8}

        };

        // transition/edge type.
        int digit_cnt = 0;
        const auto getTrans = [&digit_cnt] (const char c)
        {
            if (isdigit (c))
            {
                digit_cnt++;
                return 0;
            }
            else if (c == '.')
                return 1;
            else if (c == 'e')
                return 2;
            else if (c == '+' || c == '-')
                return 3;
            else if (c == ' ')
                return 4;
            else
                return -1;
        };

        // final states.
        vector <int> final_states ({-1,-1,2,3,-1,5,-1,7,8});

        // remember last and current states.
        int prev_state, state = 0;

        // for each char.
        for (char c : s)
        {
            // get transition type.
            int trans = getTrans (c);

            // bad transition
            if (trans == -1)
                return false;

            // save previous state.
            prev_state = state;
            state = automata [state][trans];

            // no state for transition
            if (state == -1)
                return false;

            // ".e" prefix ||  ". "  
            if (state == 4 || state == 8)
                if (digit_cnt == 0)
                    return false;
        }

        // "."
        if (state == 3 && prev_state < 2)
            return false;

        // make sure state is final.
        return final_states [state] != -1;
    }
};

