class Solution {
    public boolean isNumber(String s) {
        HashMap<Integer, HashMap<String, Integer>> stateMachine = buildStateMachine();

        int state = 0;
        for(char ch : s.toCharArray()) {
            String op = charToOp(ch);

            if (!stateMachine.get(state).containsKey(op)) {
                return false;
            }

            state = stateMachine.get(state).get(op);
        }

        return state == 1 || state == 4 || state == 7;
    }

    private String charToOp(char ch) {
        switch(ch) {
            case '.':
                return "decimal";
            case '+':
            case '-':
                return "sign";
            case 'e':
            case 'E':
                return "e/E";
            default:
                if ('0' <= ch && ch <= '9') {
                    return "digit";
                } else {
                    return "";
                }
        }
    }

    private HashMap<Integer, HashMap<String, Integer>> buildStateMachine() {
        HashMap<Integer, HashMap<String, Integer>> stateMachine = new HashMap<>();

        for(int i = 0; i < 8; i++) {
            stateMachine.put(i, new HashMap<String, Integer>());
        }

        stateMachine.get(0).put("digit", 1);
        stateMachine.get(0).put("sign", 2);
        stateMachine.get(0).put("decimal", 3);

        stateMachine.get(1).put("digit", 1);
        stateMachine.get(1).put("decimal", 4);
        stateMachine.get(1).put("e/E", 5);

        stateMachine.get(2).put("digit", 1);
        stateMachine.get(2).put("decimal", 3);

        stateMachine.get(3).put("digit", 4);

        stateMachine.get(4).put("digit", 4);
        stateMachine.get(4).put("e/E", 5);

        stateMachine.get(5).put("sign", 6);
        stateMachine.get(5).put("digit", 7);

        stateMachine.get(6).put("digit", 7);

