class Solution(object):
    def isNumber(self, s):
        """
        :type s: str
        :rtype: bool
        """
        slist = s.strip().split("e")
        if len(slist) > 2:
            return False
        for index, ss in enumerate(slist):
            dotlabel = False
            numlabel = False
            for i, ch in enumerate(ss):
                if not ch.isdigit():
                    if ch == "+" or ch == "-":
                        if i != 0:
                            return False
                    elif ch == ".":
                        if index != 0 or dotlabel: 
                            return False
                        dotlabel = True
                    else:
                        return False
                else:
                    numlabel = True
            if not numlabel:
                return False
        return True
