func isNumber(_ s: String) -> Bool {
	let mod = s.trimmingCharacters(in: .whitespacesAndNewlines)
	if let _ = Double(mod) {
		return true
	} else {
		if let idx = mod.firstIndex(of: "e") {
			let first = String(mod[mod.startIndex..<idx])
			let offset = mod.index(after: idx)
			let second = String(mod[offset..<mod.endIndex])
			if let _ = Double(first), let _ = Int(second) {
				return true
			}
		}
		return false
	}
}

