class Solution(object):
    def isNumber(self, s):
        """
        :type s: str
        :rtype: bool
        """

        if not s:
            return False

        s = s.strip()
        see_digit, see_dot, see_e = False, False, False

        for i, c in enumerate(s):

            # If sign appears as not the first character.
            if c in "+-":
                if i > 0 and s[i-1] != 'e':
                    return False

            # If we see a dot and have already seen a dot or e, false.
            elif c == '.':
                if see_dot or see_e:
                    return False
                see_dot = True

            # If we see an 'e', return false if we have see no digits yet or
            # have already seen an 'e' before. Also, we should expect the next
            # character to be a sign or a digit. So, clear the digit flag.
            elif c == 'e':
                if see_e or not see_digit:
                    return False
                see_e = True
                see_digit = False

            # If we see a digit, set see_digit to true.
            elif c.isdigit():
                see_digit = True

            # If any other character, return False
            else:
                return False

        # We should return whether we have seen a digit at the end.
        return see_digit
