class Solution {
public:

    bool isDigit(char c) {
        return c >= '0' && c <= '9';
    }

    bool isExpo(char c) {
        return c == 'e' || c == 'E';
    }

    bool isSign(char c) {
        return c == '+' || c == '-';
    }

    bool isNumber(string s) {

        bool aResult = false;

        int aIndex = 0;
        while (aIndex < s.size() && s[aIndex] == ' ') { aIndex++; }
        if (aIndex >= s.size()) { return false; }

        int aExpoCount = 0;

        NUMBER_BEGIN:

        if (isSign(s[aIndex])) { aIndex++; }
        if (aIndex >= s.size()) { return false; }

        while (aIndex < s.size() && isDigit(s[aIndex])) {
            aResult = true;
            aIndex++;
        }
        if (aIndex >= s.size()) { return aResult; }

        if (s[aIndex] == '.' && aExpoCount == 0) { aIndex++; }

        while (aIndex < s.size() && isDigit(s[aIndex])) {
            aResult = true;
            aIndex++;
        }
        if (aIndex >= s.size()) { return aResult; }

        if (isExpo(s[aIndex]) && aResult == true) {
            aIndex++;
            aExpoCount++;
            if (aExpoCount > 1) { return false; }
            aResult = false;
            goto NUMBER_BEGIN;
        }

        //Trailing spaces are OKAY.
        while (aIndex < s.size() && s[aIndex] == ' ') { aIndex++; }

        //Trailing gobbelty gook is NOT OKAY.
        if (aIndex < s.size()) { return false; }

        return aResult;
    }
};
