// A parser takes a string and tries to find what it is
// looking for at the beginning. It returns the remaining
// string or undefined if the parsing failed. For example
// a digit parser when applied to "1abc" would return "abc".
// The same parser when applied to "abc" would return undefined.
type Parser = (src: string) => string | undefined;

// A simple parser that looks for a fixed piece of text.
const text =
  (find: string): Parser =>
  (src: string) =>
    src.startsWith(find) ? src.substring(find.length) : undefined;

const choice =
  (...pp: Parser[]): Parser =>
  (src: string) =>
    pp.map((p) => p(src)).find((i) => i !== undefined);

const maybe = (p: Parser) => (src: string) => p(src) ?? src;

// A sequence; apply each parser in order.
const seq =
  (...pp: Parser[]) =>
  (src: string) => {
    let remain = src;
    for (const p of pp) {
      const after = p(remain);
      if (after === undefined) return undefined;
      else remain = after;
    }
    return remain;
  };

const zeroOrMore = (pp: Parser) => (src: string) => {
  let remain: string = src;
  do {
    const after = pp(remain);
    if (after === undefined) return remain;
    else remain = after;
  } while (true);
};

const oneOrMore = (p: Parser) => seq(p, zeroOrMore(p));

const notNext = (p: Parser) => (src: string) => p(src) !== undefined ? undefined : src;

const endText = (src: string) => (src === "" ? src : undefined);

// faster than choice with 10 options
const zeroToNine = (src: string) => (src[0] >= "0" && src[0] <= "9" ? src.substring(1) : undefined);

const digits = oneOrMore(zeroToNine);

const sign = choice(text("+"), text("-"));

const integer = seq(maybe(sign), oneOrMore(zeroToNine));

const dot = text(".");
const decimal = seq(maybe(sign), choice(seq(digits, dot, maybe(digits)), seq(dot, digits)));

const e = choice(text("e"), text("E"));

const validNumber = seq(
  choice(decimal, integer),
  notNext(sign),
  maybe(seq(maybe(e), integer)),
  endText
);

const isNumber = (s: string): boolean => validNumber(s) !== undefined;
```

