class Solution {
public:
    int compareVersion(string version1, string version2) {

        auto getVersionVec = [](string& s) {
            istringstream ss(s);
            string t;
            vector<int> v;
            while (getline(ss, t, '.')) {
                v.push_back(stoi(t));
            }
            return v;
        };

        auto v1 = getVersionVec(version1), v2 = getVersionVec(version2);
        while (v1.size() != v2.size()) {
            (v1.size() < v2.size()? v1 : v2).push_back(0);
        }
        if (v1 == v2)
            return 0;
        return v1 > v2? 1 : -1;        
    }
};
