class Solution {
    public boolean canMeasureWater(int j1, int j2, int t) {

        if(j1 + j2 < t)return false;//example 2

        int[] dir = {j1 , -j1 , j2 , -j2}; //   4 options either take add water to jug1 or remove it same goes for jug 2

        Queue<Integer> q = new LinkedList<>();
        q.offer(0);
        Set<Integer> visited = new HashSet<>();
        visited.add(0);

        while(!q.isEmpty())
        {
            int sz = q.size();
            while(sz-->0)
            {
                int curr = q.poll();
                if(curr == t)return true;
                 for(int d : dir)
                 {
                    int total = d + curr;
                     if(total == t)return true;
                     if(total < 0 || total > j1 + j2)continue;
                     if(!visited.contains(total))
                     {
                          visited.add(total);
                          q.offer(total);
                     }
                 }
            }
        }return false;
    }
}

