class Solution {
    public boolean canMeasureWater(int x, int y, int z) {

        // Start with both jugs empty
        int[] start = new int[2];

        Queue<int []> queue = new LinkedList<>();
        queue.offer(start);
        Set<String> set = new HashSet<>();

        while(!queue.isEmpty()) {

            int size = queue.size();
            for(int i = 0; i<size; i++) {

                int [] cur = queue.poll();
                // Check visited
                if(set.contains(cur[0]+":"+cur[1])) continue;
                // check destination
                if(cur[0] + cur[1] == z) return true;

                set.add(cur[0]+":"+cur[1]);
                List<int []> neighbors = generateNeighbors(cur, x, y);
                for(int [] j : neighbors) {
                    queue.add(j);
                }
            }
        }

        return false;        
    }

    public List<int []> generateNeighbors(int [] cur, int x, int y){

        // All Possible States

        List<int []> temp = new ArrayList<>();

        temp.add(new int [] {0, cur[1]});
        temp.add(new int [] {x, cur[1]});
        temp.add(new int [] {cur[0], 0});
        temp.add(new int [] {cur[0], y});

        if(cur[0] > y - cur[1]) {
             temp.add(new int [] {cur[0] - y + cur[1], y});
        } else {
            temp.add(new int [] {0, cur[0] + cur[1]});
        }
        if(cur[1] > x - cur[0]) {
             temp.add(new int [] {x, cur[1] + cur[0] - x});
        } else {
             temp.add(new int [] {cur[0] + cur[1], 0});
        }
        return temp;
    }
}

