class Solution {
public:
    bool canMeasureWater(int jug1Capacity, int jug2Capacity, int targetCapacity) {
        // we will have 4 posibilities either to decrease the capacity of 
        // the either of the jugs so we make an array of possibilities
        int steps[]={-jug1Capacity,+jug1Capacity,-jug2Capacity,+jug2Capacity};
        // no we can have the maximum water it can store the 
        // sum of the max of both jug1 and jug2
        int z=jug1Capacity+jug2Capacity;
        // visited array to have a track
        vector<int>visited(z+1,0);
        // simple bfs
        queue<int>q;
        // initially took no loss no gain
        q.push(0);
        while(!q.empty()){
            int node=q.front();
            q.pop();
            // marked the node as visited
            visited[node]=1;
            if(node==targetCapacity) return true;
            for(int i=0;i<4;i++){
                // taking the newnode value after increasing / decreasing 
                // to have all the posibilities
                int newnode=node+steps[i];
                // we can have boundary condition 
                if(newnode>=0 and newnode<=z and !visited[newnode]){
                q.push(newnode);
              }
            }
        }
        return false;
    }
};
