class Solution {
public:
    bool canMeasureWater(int jug1Capacity, int jug2Capacity, int targetCapacity) {
        int x = jug1Capacity, y = jug2Capacity, z = x + y;
        vector<bool> visited(z + 1, false);
        vector<int> dirs = {x, -x, y, -y};
        queue<int> q;

        q.push(0);
        while (!q.empty()) {
            int currCap = q.front();
            q.pop();

            if (visited[currCap]) {
                continue;
            }
            visited[currCap] = true;

            if (currCap == targetCapacity) {
                return true;
            }

            for (auto &dir : dirs) {
                int nextCap = currCap + dir;
                if (nextCap < 0 || visited.size() <= nextCap) {
                    continue;
                }
                q.push(nextCap);
            }
        }
        return false;
    }
};
```

