**1. using memoization**
```
class Solution {
private:
    bool solve(int &j1, int &j2, int &t, int jug, vector<int> &dp){
	    //base case
        if(jug<0 || jug>j1+j2){
            return false;
        }

        if(jug == t){
            return true;
        }

        if(dp[jug]!=-1) return dp[jug];
        dp[jug] = false;

        //there can only be four actions one can do to jug

        //add j1 to j
        if(solve(j1, j2, t, jug+j1, dp)) return dp[jug] = true;

        //add j2 to j
        if(solve(j1, j2, t, jug+j2, dp)) return dp[jug] = true;

        //substract j1 from j
        if(solve(j1, j2, t, jug-j1, dp)) return dp[jug] = true;

        //substract j2 from j
        if(solve(j1, j2, t, jug-j2, dp)) return dp[jug] = true;

        return dp[jug] = false;
    }

public:
    bool canMeasureWater(int jug1, int jug2, int target) {
        if(jug1 + jug2 < target) return false;
        int jug = 0; //consider jug as the one jug which will filled insteead of filling j1 / j2.
        vector<int> dp(jug1+jug2+1, -1); //memoization
        return solve(jug1, jug2, target, jug, dp);
    }
};
```

