# BFS

	class Solution {
	public:
    bool canMeasureWater(int jug1Capacity, int jug2Capacity, int targetCapacity) 
    {
        int dx[4]={jug1Capacity,-jug1Capacity,jug2Capacity,-jug2Capacity};

        //If targetCapacity liters of water are measurable, it must be within one or both       buckets by the end.

        if(jug1Capacity+jug2Capacity<targetCapacity)
            return 0;

        //for initial state 0
        queue<int>que;
        //for visited
        set<int>vis;
        //can use vector too of size jug1Capacity+jug2Capacity;

        que.emplace(0);
        vis.emplace(0);

        while(!que.empty())
        {

            int curr=que.front();
            // cout<<curr<<endl;
            que.pop();

            if(curr==targetCapacity)
                return 1;

            //direction
            for(int i=0;i<4;++i)
            {
                int total=dx[i]+curr;

                //boundry case
                if(total<0 || total>jug1Capacity+jug2Capacity)
                    continue;

                //push only when not visited
                if(!vis.count(total))
                {
                    vis.emplace(total);
                    que.emplace(total);
                }
            }
        }

        return 0;
    }
	};

