class Solution:

    def canMeasureWater(
        self, 
        jug1Capacity: int, 
        jug2Capacity: int, 
        targetCapacity: int
    ) -> bool:

        if targetCapacity > jug1Capacity + jug2Capacity:
            return False

        seen = {(0, 0)}
        jug1 = jug2 = 0

        while True:

            if targetCapacity in {jug1, jug2, jug1 + jug2}:
                return True

            # fill jug1
            if jug1 == 0:
                jug1 = jug1Capacity
            # empty jug2
            elif jug2 == jug2Capacity:
                jug2 = 0
            # pour from jug1 to jug2
            else:
                pour_to_jug2 = min(jug1, (jug2Capacity - jug2))
                jug1 -= pour_to_jug2
                jug2 += pour_to_jug2

            if (jug1, jug2) in seen:
                break

            seen.add((jug1, jug2))

        return False
