            if(pa.first == target || pa.second == target || pa.first+pa.second == target) return true;

            // fill first jug completly full
            if(hs.count({a,pa.second}) == 0)  q.push({a,pa.second});

            // fill second jug completly full
            if(hs.count({pa.first,b}) == 0) q.push({pa.first,b});

            // pour water from jug1 to jug2 until jug2 is full or jug1 is empty
            int d1 = min(pa.first,b-pa.second);
            pair<int,int> pa1 = {pa.first-d1,pa.second+d1};
            if(hs.count(pa1) == 0)  q.push(pa1);

            // pour water from jug2 to jug1 until jug1 is full or jug2 is empty
            int d2 = min(a-pa.first,pa.second);
            pair<int,int> pa2 = {pa.first+d2,pa.second-d2};
            if(hs.count(pa2) == 0) q.push(pa2);

            // empty jug1
            if(hs.count({0,pa.second}) == 0)  q.push({0,pa.second});

            // empty jug2
            if(hs.count({pa.first,0}) == 0)  q.push({pa.first,0});
        }
        return false;
    }
};
```

