class Solution {
public:
    bool canMeasureWater(int a, int b, int c) {
        if(c>a+b)return false;
        for(int i=1;i<=max(a,max(b,c));i++)
        {
           long long int f= (long long int)a*(long long int)i;
           long long  int z= abs((long long int)c-f);
            if(z%b==0)
                return true;

        }
        return false;

    }
};
